import problem_3_5.customers;
import ballerina/io;
import problem_3_5.sales;

type Q "Q1"|"Q2"|"Q3"|"Q4";

type Quarter [int, Q];

type Sales record {|
    *sales:Sales;
    readonly string customerId;
|};

type Customer record {|
    *customers:Customer;
    readonly string id;
|};

function findTopXCustomers(Quarter[] quarters, int x) returns customers:Customer[]|error {
    // TODO Implement your logic here
    sales:Client salesClient = check new("http://localhost:8080/sales");
    customers:Client customersClient = check new("http://localhost:8080/customers");

    customers:Customer[] customersArray = check customersClient->get();
    Customer[] customersReadOnly = check customersArray.cloneWithType();
    table<Sales> key(customerId) salesTable = table [];
    table<Customer> key(id) customerTable = table key(id) from var customer in customersReadOnly select customer;

    foreach var quarter in quarters {
        sales:Sales[] quarterSales = check salesClient->get(quarter[0], quarter[1]);
        Sales[] quarterSalesReadOnly = check quarterSales.cloneWithType();
        foreach Sales sales in quarterSalesReadOnly {
            if salesTable.hasKey(sales.customerId) {
                Sales sale = salesTable.get(sales.customerId);
                sale.amount += sales.amount;
                continue;
            }
            salesTable.add(sales);
        }
    }

    record {|string customerId; decimal amount;|}[] test = from var {amount, customerId} in salesTable order by amount descending select {customerId, amount};
    io:println(test);
    return from var {amount, customerId} in salesTable order by amount descending limit x join Customer customer in customerTable on customerId equals customer.id select customer;
}

import ballerina/http;
import ballerina/io;
import ballerina/log;

# Type to represent a quarter
type Q "Q1"|"Q2"|"Q3"|"Q4";

# Represents total sales per customer
type Sales record {|
    # Customer's ID
    readonly string customerId;
    # Total amount of sales for the customer
    readonly decimal amount;
    # Quarter in which these sales were reported
    readonly Q quarter;
    # Year for which these sales values belong
    readonly int year;
|};

type SalesResponse record {|
    *http:Ok;

    map<string> headers = {
        "content-type": "application/json"
    };
    Sales[] body;
|};

isolated service /sales on httpEP {

    private final table<Sales> key(customerId, year, quarter) sales = table [];

    function init() returns error? {
        json data = check io:fileReadJson("tests/resources/sales.json");
        if data is json[] {
            foreach json item in data {
                Sales sales = check item.fromJsonWithType(Sales);
                self.sales.add(sales);
            }
        }
    }

    # Get total sales
    #
    # + year - Query param to specify the year  
    # + quarter - Optional query param quarter
    # + return - List of sales
    isolated resource function get .(int year, string? quarter) returns SalesResponse|http:BadRequest {
        Q? q = ();
        if quarter is string {
            Q|error val = quarter.ensureType(Q);
            if val is error {
                log:printWarn("Invalid quarter", q = quarter);
                return {body: "Invalid quarter provided"};
            }

            q = val;
        }

        lock {
            Sales[] filteredSales = from var sales in self.sales
                where sales.year == year
                where quarterMatches(q, sales.quarter)
                select sales;
            return <SalesResponse>{
                body: filteredSales.cloneReadOnly()
            };
        }
    }
}

isolated function quarterMatches(Q? q1, Q q2) returns boolean {
    return q1 == () || q1 == q2;
}

